mapper.update:
update cn_note set
cn_note_title=#{note_title},
cn_note_body=#{note_body},
cn_note_last_modify_time=#{note_last_modify_time}
where cn_note_id=#{note_id};

update cn_note set 
cn_note_status_id = '2'
where cn_note_id=#{id};

update cn_note set
cn_note_status_id='4'
where cn_note_id=#{id};
